    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        if (dados.Login(txtRg.getText(), txtSenha.getText())) {//A partir da funcao login da classe manipula banco ele checa se o rg e a senha sao iguais e retorna um boolean
            Principal menu = new Principal();//Se for verdadeiro abre a tela principal
            menu.setVisible(true);
            dispose();
        } else {
            JOptionPane.showMessageDialog(null, "Rg ou Senha Invalidos");
        }
    }//GEN-LAST:event_jButton1ActionPerformed

